// src/pages/Dashboard/ChatPage.jsx
import React, { useState, useEffect, useRef } from "react";
import { Send, Loader2 } from "lucide-react";

const ChatPanel = ({ messages, isSending, onSend }) => {
  const [text, setText] = useState("");
  const bottomRef = useRef(null);

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, isSending]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (text.trim()) {
      onSend(text.trim());
      setText("");
    }
  };

  return (
    <div className="flex flex-col h-full p-4">
      <div className="flex-grow overflow-y-auto space-y-4 pr-2">
        {messages.map((msg, i) => (
          <div key={i} className={`flex ${msg.sender === "user" ? "justify-end" : "justify-start"}`}>
            <div
              className={`max-w-2xl p-3 rounded-xl ${
                msg.sender === "user"
                  ? "bg-indigo-600 text-white"
                  : "bg-gray-700 text-gray-100"
              }`}
            >
              <p className="whitespace-pre-wrap text-sm">{msg.text}</p>
            </div>
          </div>
        ))}

        {isSending && (
          <div className="flex justify-start">
            <div className="p-3 bg-gray-700 rounded-xl flex items-center space-x-2">
              <Loader2 className="w-4 h-4 animate-spin text-indigo-400" />
              <span>Molly escribiendo...</span>
            </div>
          </div>
        )}

        <div ref={bottomRef} />
      </div>

      <form onSubmit={handleSubmit} className="flex items-center mt-4 p-3 bg-gray-800 rounded-xl">
        <input
          className="flex-grow bg-transparent text-gray-200 outline-none"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Escribe tu mensaje..."
        />
        <button
          className="ml-3 p-3 rounded-full bg-indigo-600 hover:bg-indigo-500"
          disabled={!text.trim() || isSending}
        >
          {isSending ? <Loader2 className="w-5 h-5 animate-spin" /> : <Send />}
        </button>
      </form>
    </div>
  );
};

export default ChatPanel;
