// src/pages/Auth/LoginPage.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { LogIn, User, Lock, Bot } from 'lucide-react';
import { useAppContext } from '../../context/AppContext.jsx'; // FIX: Se añadió .jsx

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { login } = useAppContext();
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');

    // Intenta iniciar sesión usando la función del contexto
    const success = login(username, password);

    if (success) {
      // Redirigir a la ruta principal del dashboard (ChatPage)
      navigate('/', { replace: true });
    } else {
      setError('Credenciales inválidas. Usa "admin" / "admin"');
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 flex items-center justify-center p-4">

      {/* Tarjeta de Login Centrada - Estilo Dark Mode/Índigo */}
      <div className="w-full max-w-md bg-gray-800 p-8 md:p-10 rounded-2xl shadow-2xl border border-gray-700/50">

        {/* Encabezado con Icono de Molly */}
        <div className="flex flex-col items-center mb-8">
          <div className="p-4 bg-indigo-600 rounded-full shadow-lg shadow-indigo-500/30 mb-4">
            <Bot className="w-8 h-8 text-white" />
          </div>
          <h1 className="text-3xl font-bold text-white mb-2">
            Bienvenido a Molly
          </h1>
          <p className="text-gray-400 text-center">
            Asistente de Ciberseguridad IA. Inicia sesión para continuar.
          </p>
        </div>

        {/* Formulario */}
        <form onSubmit={handleSubmit} className="space-y-6">

          {/* Campo de Usuario */}
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-1" htmlFor="username">
              Usuario
            </label>
            <div className="relative">
              <User className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-500" />
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="admin"
                className="w-full pl-10 pr-4 py-3 bg-gray-700 text-white border border-gray-600 rounded-xl focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-150"
                required
              />
            </div>
          </div>

          {/* Campo de Contraseña */}
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-1" htmlFor="password">
              Contraseña
            </label>
            <div className="relative">
              <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-500" />
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="admin"
                className="w-full pl-10 pr-4 py-3 bg-gray-700 text-white border border-gray-600 rounded-xl focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-150"
                required
              />
            </div>
          </div>

          {/* Mensaje de Error */}
          {error && (
            <div className="bg-red-900/40 border border-red-700 text-red-300 p-3 rounded-xl text-sm text-center">
              {error}
            </div>
          )}

          {/* Botón de Login (Acento Índigo) */}
          <button
            type="submit"
            className="w-full flex items-center justify-center py-3 px-4 bg-indigo-600 hover:bg-indigo-700 text-white font-semibold rounded-xl transition duration-200 shadow-md shadow-indigo-500/30 disabled:bg-gray-600 disabled:shadow-none"
            disabled={!username || !password}
          >
            <LogIn className="w-5 h-5 mr-2" />
            Iniciar Sesión
          </button>
        </form>

        {/* Pie de Página */}
        <div className="mt-8 text-center text-sm text-gray-500">
          <p>&copy; {new Date().getFullYear()} Molly AI. Todos los derechos reservados.</p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
