// src/App.jsx
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";

import LoginPage from "./pages/Auth/LoginPage";
import ChatPage from "./pages/Dashboard/ChatPage";
import DashboardPage from "./pages/Dashboard/DashboardPage";
import VulnerabilitiesPage from "./pages/Dashboard/VulnerabilitiesPage";
import AssetsPage from "./pages/Dashboard/AssetsPage";
import ReportsPage from "./pages/Dashboard/ReportsPage";
import SummariesPage from "./pages/Dashboard/SummariesPage";
import MonitoringPage from "./pages/Dashboard/MonitoringPage";

import ProtectedRoute from "./components/common/ProtectedRoute";
import MainLayout from "./layouts/MainLayout";

export default function App() {
    return (
        <BrowserRouter>
            <Routes>

                {/* Redirigir al login */}
                <Route path="/" element={<Navigate to="/login" />} />

                {/* Login */}
                <Route path="/login" element={<LoginPage />} />

                {/* Rutas protegidas */}
                <Route
                    path="/dashboard"
                    element={
                        <ProtectedRoute>
                            <MainLayout />
                        </ProtectedRoute>
                    }
                >
                    {/* Rutas dentro del dashboard â†’ estas SI se renderizan en <Outlet/> */}
                    <Route path="chat" element={<ChatPage />} />
                    <Route path="dashboard" element={<DashboardPage />} />
                    <Route path="vulnerabilities" element={<VulnerabilitiesPage />} />
                    <Route path="assets" element={<AssetsPage />} />
                    <Route path="reports" element={<ReportsPage />} />
                    <Route path="summaries" element={<SummariesPage />} />
                    <Route path="monitoring" element={<MonitoringPage />} />

                    {/* Default */}
                    <Route index element={<Navigate to="dashboard" />} />
                </Route>

            </Routes>
        </BrowserRouter>
    );
}
