// src/App.jsx
import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import { AppProvider, useAppContext } from './context/AppContext';

// Páginas Importadas
import LoginPage from './pages/Auth/LoginPage';
import ChatPage from './pages/Dashboard/ChatPage'; 

// Componente para proteger las rutas (Simula ProtectedRoute.jsx)
const ProtectedRoute = ({ element: Element, ...rest }) => {
  const { isLoggedIn } = useAppContext();
  // Si no está logueado, redirige a la página de login.
  return isLoggedIn ? Element : <Navigate to="/login" replace />;
};

const AppRoutes = () => {
  return (
    <Routes>
      {/* Ruta de Login (Pública) */}
      <Route path="/login" element={<LoginPage />} />

      {/* Rutas Protegidas (Requieren Login) */}
      {/* La ruta raíz redirige al chat por defecto */}
      <Route path="/" element={<ProtectedRoute element={<ChatPage />} />} />

      {/* Aquí irán las demás páginas (Dashboard, Reports, etc.) */}
      {/* Ejemplo: <Route path="/dashboard" element={<ProtectedRoute element={<DashboardPage />} />} /> */}

      {/* Manejo de rutas no encontradas */}
      <Route path="*" element={<Navigate to="/" replace />} />
    </Routes>
  );
};


const App = () => {
  return (
    // Envolvemos toda la aplicación con el proveedor de contexto
    <AppProvider>
      <BrowserRouter>
        <AppRoutes />
      </BrowserRouter>
    </AppProvider>
  );
};

export default App;
