// src/layout/MainLayout.jsx
import React, { useState } from "react";
import { 
  Menu, Bot, Settings, Globe, BarChart3, Shield, Briefcase, 
  FileText, Layers, Zap, Bell, Terminal, LogOut
} from "lucide-react";

const menuItems = [
  { name: "IA", icon: Bot, path: "/chat" },
  { name: "Automatizaciones", icon: Terminal, path: "/automatizations" },
  { name: "OSINT", icon: Globe, path: "/osint" },
  { name: "Dashboard", icon: BarChart3, path: "/dashboard" },
  { name: "Vulnerabilidades", icon: Shield, path: "/vulnerabilities" },
  { name: "Activos", icon: Briefcase, path: "/assets" },
  { name: "Reportes", icon: FileText, path: "/reports" },
  { name: "Informes", icon: Layers, path: "/summaries" },
  { name: "Monitorizacion", icon: Zap, path: "/monitoring" },
  { name: "Notificaciones", icon: Bell, path: "/notifications" },
  { name: "Configuracion", icon: Settings, path: "/settings" },
];

const MainLayout = ({ children, title }) => {
  const [isSidebarExpanded, setIsSidebarExpanded] = useState(true);

  return (
    <div className="flex h-screen bg-gray-900 text-white font-sans">

      {/* SIDEBAR */}
      <aside
        className={`flex flex-col bg-gray-800 transition-all duration-300 ${
          isSidebarExpanded ? "w-64" : "w-20"
        } p-4 shadow-2xl`}
      >
        {/* LOGO + BOTON */}
        <div
          className={`flex items-center mb-8 h-8 ${
            isSidebarExpanded ? "justify-between" : "justify-center"
          }`}
        >
          <div
            className={`text-2xl font-bold text-indigo-400 ${
              !isSidebarExpanded && "hidden"
            }`}
          >
            MOLLY
          </div>

          <button
            onClick={() => setIsSidebarExpanded(!isSidebarExpanded)}
            className="p-2 rounded-lg text-gray-400 hover:bg-gray-700 hover:text-white"
          >
            <Menu className="w-6 h-6" />
          </button>
        </div>

        {/* NAV */}
        <nav className="flex-grow space-y-2">
          {menuItems.map((item) => (
            <a
              key={item.path}
              href={item.path}
              className={`flex items-center w-full p-3 rounded-xl transition-all duration-200
                text-gray-300 hover:bg-gray-700 hover:text-white
                ${isSidebarExpanded ? "justify-start" : "justify-center"}
              `}
            >
              <item.icon className={`w-5 h-5 ${isSidebarExpanded && "mr-3"}`} />
              {isSidebarExpanded && (
                <span className="text-sm font-medium">{item.name}</span>
              )}
            </a>
          ))}
        </nav>

        {/* LOGOUT */}
        <div className="mt-8 pt-4 border-t border-gray-700">
          <button
            className={`flex items-center w-full p-3 rounded-xl text-red-400 hover:bg-gray-700 hover:text-red-300
              ${isSidebarExpanded ? "justify-start" : "justify-center"}
            `}
            onClick={() => alert("Cerrar sesion")}
          >
            <LogOut className={`w-5 h-5 ${isSidebarExpanded && "mr-3"}`} />
            {isSidebarExpanded && <span>Cerrar Sesion</span>}
          </button>
        </div>
      </aside>

      {/* DERECHA */}
      <main className="flex-grow flex flex-col overflow-hidden">

        {/* HEADER */}
        <header className="flex items-center justify-between p-4 bg-gray-800 shadow-xl border-b border-gray-700">
          <h1 className="text-2xl font-semibold">{title}</h1>

          <div className="flex items-center space-x-4">
            <Settings className="w-5 h-5 text-gray-400 hover:text-white cursor-pointer" />
            <div className="w-8 h-8 rounded-full bg-indigo-600 flex items-center justify-center">
              L
            </div>
          </div>
        </header>

        {/* CONTENIDO */}
        <section className="flex-grow overflow-auto bg-gray-900 p-6">
          {children}
        </section>
      </main>
    </div>
  );
};

export default MainLayout;
