// src/components/common/Siderbar.jsx
import React from "react";
import { Menu, LogOut } from "lucide-react";

const Sidebar = ({ activeSection, setActiveSection, isExpanded, setIsExpanded, menuItems }) => {
  return (
    <aside 
      className={`flex flex-col bg-gray-800 transition-all duration-300 ${
        isExpanded ? "w-64" : "w-20"
      } p-4 shadow-2xl`}
    >

      {/* Logo + Toggle */}
      <div className={`flex items-center mb-8 h-8 ${isExpanded ? "justify-between" : "justify-center"}`}>
        {isExpanded && <div className="text-2xl font-bold text-indigo-400">MOLLY</div>}
        <button
          onClick={() => setIsExpanded(!isExpanded)}
          className="p-2 rounded-lg text-gray-400 hover:bg-gray-700 hover:text-white transition-colors"
        >
          <Menu className="w-6 h-6" />
        </button>
      </div>

      {/* Navigation */}
      <nav className="flex-grow space-y-2">
        {menuItems.map((item) => (
          <button
            key={item.path}
            onClick={() => setActiveSection(item.path)}
            className={`flex items-center w-full p-3 rounded-xl transition-all duration-200 
            ${isExpanded ? "justify-start" : "justify-center"} 
            ${
              activeSection === item.path
                ? "bg-indigo-600 text-white shadow-md"
                : "text-gray-300 hover:bg-gray-700 hover:text-white"
            }`}
          >
            <item.icon className={`w-5 h-5 ${isExpanded && "mr-3"}`} />
            {isExpanded && <span className="text-sm font-medium">{item.name}</span>}
          </button>
        ))}
      </nav>

      {/* Logout */}
      <div className="mt-8 pt-4 border-t border-gray-700">
        <button
          onClick={() => alert("Cerrar sesion (simulado)")}
          className={`flex items-center w-full p-3 rounded-xl transition-all duration-200
          ${isExpanded ? "justify-start" : "justify-center"} 
          text-red-400 hover:bg-gray-700 hover:text-red-300`}
        >
          <LogOut className={`w-5 h-5 ${isExpanded && "mr-3"}`} />
          {isExpanded && <span className="text-sm font-medium">Cerrar Sesion</span>}
        </button>
      </div>

    </aside>
  );
};

export default Sidebar;
