import React, { createContext, useContext, useState } from 'react';

// 1. Crear el contexto
const AppContext = createContext();

// Hook personalizado para usar el contexto fácilmente
export const useAppContext = () => {
  return useContext(AppContext);
};

// 2. Proveedor del Contexto
export const AppProvider = ({ children }) => {
  // Estado para rastrear si el usuario está autenticado
  const [isLoggedIn, setIsLoggedIn] = useState(false); 

  // Simulación de la función de login
  const login = (username, password) => {
    // Credenciales de prueba: admin / admin
    if (username === 'admin' && password === 'admin') {
      setIsLoggedIn(true);
      return true;
    }
    return false;
  };

  const logout = () => {
    setIsLoggedIn(false);
  };

  const contextValue = {
    isLoggedIn,
    login,
    logout,
    // Aquí podemos agregar otros estados globales más adelante (e.g., activeSection, chatSessionId)
  };

  return (
    <AppContext.Provider value={contextValue}>
      {children}
    </AppContext.Provider>
  );
};
