// src/pages/Auth/LoginPage.jsx
import { useState, useContext } from "react";
import { loginRequest } from "../../api/mollyApi";
import { AppContext } from "../../context/AppContext";
import { useNavigate } from "react-router-dom";

export default function LoginPage() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();
    const { login } = useContext(AppContext);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const isValid = await loginRequest(username, password);

        if (isValid) {
            login(username);
            navigate("/chat");
        } else {
            alert("Credenciales incorrectas");
        }
    };

    return (
        <div className="min-h-screen bg-gray-900 flex items-center justify-center">
            <div className="bg-gray-800 p-10 rounded-xl shadow-xl w-full max-w-md">
                <h2 className="text-3xl text-center font-bold text-white mb-6">
                    Bienvenido a Molly
                </h2>

                <form onSubmit={handleSubmit} className="space-y-4">
                    <input
                        className="w-full p-3 rounded bg-gray-700 text-white"
                        placeholder="Usuario"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />

                    <input
                        type="password"
                        className="w-full p-3 rounded bg-gray-700 text-white"
                        placeholder="Contrasena"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />

                    <button
                        type="submit"
                        className="w-full bg-indigo-600 hover:bg-indigo-700 py-3 rounded-lg text-white font-semibold"
                    >
                        Iniciar Sesion
                    </button>
                </form>
            </div>
        </div>
    );
}
