import { useState } from "react";
import { LogIn, User, Lock, Bot } from "lucide-react";

export default function LoginPage() {
  const [error, setError] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    const user = e.target.username.value;
    const pass = e.target.password.value;

    if (user === "admin" && pass === "admin") {
      setError(false);
      alert("Inicio de sesion exitoso (simulado)");
    } else {
      setError(true);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#0d1117] px-4">
      <div className="w-full max-w-md bg-[#161b22] border border-[#30363d] rounded-xl p-10 shadow-2xl text-center">

        {/* Logo */}
        <div className="flex justify-center mb-6">
          <div className="bg-indigo-600 p-3 rounded-full shadow-[0_4px_20px_rgba(108,92,231,0.5)]">
            <Bot className="w-10 h-10 text-white" />
          </div>
        </div>

        <h1 className="text-3xl font-bold text-white mb-2">Bienvenido a Molly</h1>
        <p className="text-sm text-gray-400 mb-8">
          Asistente de Ciberseguridad IA. Inicia sesion para continuar.
        </p>

        <form onSubmit={handleSubmit} className="flex flex-col gap-6 text-left">

          {/* Usuario */}
          <div>
            <label className="text-gray-400 text-sm block mb-2">Usuario</label>
            <div className="relative">
              <User className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-500 h-5 w-5" />
              <input
                name="username"
                type="text"
                placeholder="admin"
                className="w-full pl-10 pr-3 py-3 bg-[#21262d] border border-[#30363d] rounded-lg text-white placeholder-gray-500 outline-none focus:border-indigo-500 focus:ring-2 focus:ring-indigo-600"
                required
              />
            </div>
          </div>

          {/* Password */}
          <div>
            <label className="text-gray-400 text-sm block mb-2">Contrasena</label>
            <div className="relative">
              <Lock className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-500 h-5 w-5" />
              <input
                name="password"
                type="password"
                placeholder="admin"
                className="w-full pl-10 pr-3 py-3 bg-[#21262d] border border-[#30363d] rounded-lg text-white placeholder-gray-500 outline-none focus:border-indigo-500 focus:ring-2 focus:ring-indigo-600"
                required
              />
            </div>
          </div>

          {/* Error */}
          {error && (
            <div className="bg-red-900/30 border border-red-600 text-red-400 p-3 rounded-lg text-sm">
              Credenciales invalidas. Usa "admin" / "admin".
            </div>
          )}

          {/* Botón */}
          <button
            type="submit"
            className="w-full flex items-center justify-center gap-2 bg-indigo-600 hover:bg-indigo-500 transition-all shadow-lg py-3 rounded-lg text-white text-base font-semibold active:translate-y-[1px]"
          >
            <LogIn className="w-5 h-5" />
            Iniciar Sesion
          </button>
        </form>

        <div className="text-gray-500 text-xs mt-8">
          © 2024 Molly AI. Todos los derechos reservados.
        </div>
      </div>
    </div>
  );
}
