import { useState } from "react";
import { useAppContext } from "../../context/AppContext";
import { loginRequest } from "../../api/mollyApi";

export default function LoginPage() {
    const { setAuth } = useAppContext();
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();

        const ok = await loginRequest(username, password);
        if (ok) {
            setAuth(true);
        } else {
            setError("Credenciales invalidas");
        }
    };

    return (
        <div className="w-full min-h-screen bg-gray-900 flex items-center justify-center px-4">
            <div className="bg-gray-800 border border-gray-700 p-8 rounded-xl shadow-xl w-full max-w-md">

                <div className="flex items-center justify-center mb-6">
                    <div className="bg-indigo-600 rounded-full p-4 shadow-lg">
                        <svg xmlns="http://www.w3.org/2000/svg" className="w-10 h-10 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                            <path d="M12 8V4H8" />
                            <path d="M22 7.2L16.4 1.6C16 1.2 15.4 1 14.8 1H9.2C8.6 1 8 1.2 7.6 1.6L2 7.2V11H22V7.2Z" />
                            <path d="M2 11V21H22V11H2Z" />
                            <path d="M14 21V17H10V21" />
                            <path d="M8 17H16" />
                        </svg>
                    </div>
                </div>

                <h1 className="text-white text-2xl font-semibold text-center mb-2">Bienvenido a Molly</h1>
                <p className="text-gray-300 text-center text-sm mb-6">
                    Inicia sesion para continuar
                </p>

                <form onSubmit={handleSubmit} className="flex flex-col gap-4">

                    <div>
                        <label className="text-gray-300 text-sm mb-1 block">Usuario</label>
                        <input
                            type="text"
                            className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-lg text-white focus:border-indigo-500 focus:ring-1 focus:ring-indigo-600 outline-none"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            placeholder="admin"
                        />
                    </div>

                    <div>
                        <label className="text-gray-300 text-sm mb-1 block">Contrasena</label>
                        <input
                            type="password"
                            className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-lg text-white focus:border-indigo-500 focus:ring-1 focus:ring-indigo-600 outline-none"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="admin"
                        />
                    </div>

                    {error && (
                        <div className="bg-red-900 text-red-300 border border-red-700 p-2 rounded text-sm">
                            {error}
                        </div>
                    )}

                    <button
                        type="submit"
                        className="w-full bg-indigo-600 hover:bg-indigo-500 transition py-2 rounded-lg text-white font-semibold"
                    >
                        Iniciar Sesion
                    </button>
                </form>

                <p className="text-gray-500 text-xs text-center mt-6">
                    Â© 2024 Molly AI
                </p>
            </div>
        </div>
    );
}
