// src/components/common/Siderbar.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

import {
  Menu, Bot, Settings, Globe, BarChart3, Shield,
  Briefcase, FileText, Layers, Bell, Terminal,
  Zap, LogOut
} from "lucide-react";

const menuItems = [
  { name: "IA", icon: Bot, path: "/dashboard/chat" },
  { name: "Automatizaciones", icon: Terminal, path: "/dashboard/automatizations" },
  { name: "OSINT", icon: Globe, path: "/dashboard/osint" },
  { name: "Dashboard", icon: BarChart3, path: "/dashboard" },
  { name: "Vulnerabilidades", icon: Shield, path: "/dashboard/vulnerabilities" },
  { name: "Activos", icon: Briefcase, path: "/dashboard/assets" },
  { name: "Reportes", icon: FileText, path: "/dashboard/reports" },
  { name: "Informes", icon: Layers, path: "/dashboard/summaries" },
  { name: "Monitorización", icon: Zap, path: "/dashboard/monitoring" },
  { name: "Notificaciones", icon: Bell, path: "/dashboard/notifications" },
  { name: "Configuración", icon: Settings, path: "/dashboard/settings" },
];

const Sidebar = ({ active }) => {
  const [expanded, setExpanded] = useState(true);
  const navigate = useNavigate();

  return (
    <aside
      className={`flex flex-col bg-gray-800 transition-all duration-300 ${
        expanded ? "w-64" : "w-20"
      } p-4`}
    >
      <div
        className={`flex items-center mb-8 h-8 ${
          expanded ? "justify-between" : "justify-center"
        }`}
      >
        {expanded && (
          <div className="text-2xl font-bold text-indigo-400">MOLLY</div>
        )}

        <button
          onClick={() => setExpanded(!expanded)}
          className="p-2 rounded-lg text-gray-400 hover:bg-gray-700"
        >
          <Menu />
        </button>
      </div>

      <nav className="flex-grow space-y-2">
        {menuItems.map((item) => (
          <button
            key={item.path}
            onClick={() => navigate(item.path)}
            className={`flex items-center w-full p-3 rounded-xl ${
              expanded ? "justify-start" : "justify-center"
            } ${
              active === item.path
                ? "bg-indigo-600 text-white"
                : "text-gray-300 hover:bg-gray-700"
            }`}
          >
            <item.icon className={`w-5 h-5 ${expanded && "mr-3"}`} />
            {expanded && <span>{item.name}</span>}
          </button>
        ))}
      </nav>

      <div className="mt-6 pt-4 border-t border-gray-700">
        <button
          onClick={() => alert("Cerrar sesión")}
          className={`flex items-center w-full p-3 rounded-xl text-red-400 hover:bg-gray-700 ${
            expanded ? "justify-start" : "justify-center"
          }`}
        >
          <LogOut className={`w-5 h-5 ${expanded && "mr-3"}`} />
          {expanded && <span>Cerrar Sesión</span>}
        </button>
      </div>
    </aside>
  );
};

export default Sidebar;
