// src/pages
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAppContext } from '../../context/AppContext';
import { LogIn } from 'lucide-react';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { login } = useAppContext();
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');

    // Intenta iniciar sesión con las credenciales (admin/admin)
    const success = login(username, password);

    if (success) {
      // Si el inicio de sesión es exitoso, redirige a la página principal (ChatPage)
      navigate('/'); 
    } else {
      setError('Credenciales incorrectas. Usa admin / admin.');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-900">
      <div className="w-full max-w-md p-8 space-y-6 bg-gray-800 rounded-2xl shadow-2xl">

        {/* Encabezado */}
        <div className="text-center">
          <h1 className="text-4xl font-extrabold text-indigo-400">MOLLY</h1>
          <p className="mt-2 text-lg text-gray-300">Asistente de Ciberseguridad</p>
        </div>

        {/* Formulario de Login */}
        <form onSubmit={handleSubmit} className="space-y-6">

          {/* Campo de Usuario */}
          <div>
            <label className="text-sm font-medium text-gray-400 block mb-2" htmlFor="username">
              Usuario
            </label>
            <input
              id="username"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="admin"
              required
              className="w-full px-4 py-3 border border-gray-700 rounded-xl bg-gray-700 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition duration-150"
            />
          </div>

          {/* Campo de Contraseña */}
          <div>
            <label className="text-sm font-medium text-gray-400 block mb-2" htmlFor="password">
              Contraseña
            </label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="******"
              required
              className="w-full px-4 py-3 border border-gray-700 rounded-xl bg-gray-700 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition duration-150"
            />
          </div>

          {/* Mensaje de Error */}
          {error && (
            <p className="text-sm font-medium text-red-400 text-center bg-red-900/30 p-2 rounded-lg">
              {error}
            </p>
          )}

          {/* Botón de Enviar */}
          <button
            type="submit"
            className="w-full flex items-center justify-center py-3 px-4 bg-indigo-600 text-white font-semibold rounded-xl hover:bg-indigo-500 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 focus:ring-offset-gray-800 transition duration-300 shadow-lg shadow-indigo-500/30"
          >
            <LogIn className="w-5 h-5 mr-2" />
            Iniciar Sesión
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
