import { useState } from "react";
import { useNavigate } from "react-router-dom";

const LoginPage = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();

    // Login mock
    if (email === "admin" && password === "1234") {
      navigate("/dashboard/chat");
    } else {
      alert("Credenciales incorrectas");
    }
  };

  return (
    <div className="w-full h-screen flex items-center justify-center bg-[#0d1117]">
      <div className="bg-[#161b22] p-8 rounded-xl shadow-lg w-full max-w-sm">
        <h2 className="text-2xl font-semibold text-center mb-6">
          Inicio de Sesion
        </h2>

        <form onSubmit={handleLogin} className="flex flex-col gap-4">
          <div>
            <label className="block mb-1 text-sm">Usuario</label>
            <input
              type="text"
              className="w-full p-2 rounded bg-[#0d1117] border border-gray-700 focus:outline-none"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="admin"
            />
          </div>

          <div>
            <label className="block mb-1 text-sm">Contrase√±a</label>
            <input
              type="password"
              className="w-full p-2 rounded bg-[#0d1117] border border-gray-700 focus:outline-none"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="1234"
            />
          </div>

          <button
            type="submit"
            className="w-full mt-2 bg-blue-600 hover:bg-blue-700 transition p-2 rounded text-white"
          >
            Ingresar
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
