import { useContext } from "react";
import AppContext from "../../context/AppContext";
import { Bot, Shield, Briefcase, FileText, Layers, Zap } from "lucide-react";

const menuItems = [
  { name: "Chat", path: "chat", icon: Bot },
  { name: "Vulnerabilidades", path: "vulnerabilities", icon: Shield },
  { name: "Activos", path: "assets", icon: Briefcase },
  { name: "Reportes", path: "reports", icon: FileText },
  { name: "Informes", path: "summaries", icon: Layers },
  { name: "Monitoreo", path: "monitoring", icon: Zap },
];

export default function Sidebar() {
  const { activePage, setActivePage } = useContext(AppContext);

  return (
    <aside className="bg-gray-800 h-full w-64 p-4">
      <nav className="space-y-2">
        {menuItems.map(item => (
          <button
            key={item.path}
            onClick={() => setActivePage(item.path)}
            className={`flex items-center w-full p-3 rounded-xl transition-all duration-200
            ${activePage === item.path 
              ? "bg-indigo-600 text-white shadow-md" 
              : "text-gray-300 hover:bg-gray-700 hover:text-white"
            }`}
          >
            <item.icon className="w-5 h-5 mr-3" />
            <span>{item.name}</span>
          </button>
        ))}
      </nav>
    </aside>
  );
}
